def ASCII():
    global steam, pan, fire

    ## Prints the first outputs
    print(steam[0] + "\n" + pan[0] + "\n" + fire[0])
    time.sleep(0.5)

    # Animates for a brief period
    for i in range(10):
        ## Moves the cursor back-up to the top of the ASCII art and reprints it
        sys.stdout.write("\r\033[2K\033[14A" + steam[i % 3] + "\n" + pan[i % 3] + "\n" + fire[i % 3])
        sys.stdout.flush()
        time.sleep(0.5)